                            "contents": "import { Button } from \"@/components/ui/button\";\nimport {\n  Card,\n  CardContent,\n  CardDescription,\n  CardFooter,\n  CardHeader,\n  CardTitle,\n} from \"@/components/ui/card\";\nimport { motion } from \"motion/react\";\n\nexport function Pricing() {\n  const containerVariants = {\n    hidden: { opacity: 0, y: 20 },\n    visible: {\n      opacity: 1,\n      y: 0,\n      transition: {\n        duration: 0.6,\n        staggerChildren: 0.2,\n      },\n    },\n  };\n\n  const itemVariants = {\n    hidden: { opacity: 0, y: 20 },\n    visible: {\n      opacity: 1,\n      y: 0,\n    },\n  };\n\n  return (\n    <motion.section\n      className=\"container px-4 py-20 mx-auto\"\n      initial=\"hidden\"\n      whileInView=\"visible\"\n      viewport={{ once: true, margin: \"-100px\" }}\n      variants={containerVariants}\n    >\n      <motion.h2\n        className=\"mb-12 text-3xl font-bold text-center\"\n        variants={itemVariants}\n      >\n        Simple, Transparent Pricing\n      </motion.h2>\n      <div className=\"grid gap-8 md:grid-cols-3\">\n        {[\n          {\n            title: \"Starter\",\n            price: \"$9\",\n            description: \"Perfect for small teams\",\n            features: [\"Up to 5 users\", \"Basic features\", \"Email support\"],\n          },\n          {\n            title: \"Pro\",\n            price: \"$29\",\n            description: \"Best for growing businesses\",\n            features: [\n              \"Up to 20 users\",\n              \"Advanced features\",\n              \"Priority support\",\n            ],\n          },\n          {\n            title: \"Enterprise\",\n            price: \"Custom\",\n            description: \"For large organizations\",\n            features: [\"Unlimited users\", \"Custom features\", \"24/7 support\"],\n          },\n        ].map((plan) => (\n          <motion.div\n            key={plan.title}\n            variants={itemVariants}\n            whileHover={{ scale: 1.02 }}\n            transition={{ type: \"spring\", stiffness: 300 }}\n          >\n            <Card className=\"border shadow-lg\">\n              <CardHeader>\n                <CardTitle>{plan.title}</CardTitle>\n                <CardDescription>{plan.description}</CardDescription>\n              </CardHeader>\n              <CardContent>\n                <p className=\"text-4xl font-bold\">{plan.price}</p>\n                <ul className=\"mt-4 space-y-2\">\n                  {plan.features.map((feature, i) => (\n                    // biome-ignore lint/suspicious/noArrayIndexKey: <explanation>\n                    <li key={i} className=\"flex items-center\">\n                      <span className=\"mr-2\">âœ“</span>\n                      {feature}\n                    </li>\n                  ))}\n                </ul>\n              </CardContent>\n              <CardFooter>\n                <Button className=\"w-full\">Get Started</Button>\n              </CardFooter>\n            </Card>\n          </motion.div>\n        ))}\n      </div>\n    </motion.section>\n  );\n}\n"
